import { ADD_MOVIE_LIST, REMOVE_MOVIE_LIST } from "../Action/movieAction";

const initialState = {
  movie: [],
};

const movieReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_MOVIE_LIST:
      if (state.movie.find((movie) => movie.id === action.payload.id))
        return state;
      return {
        ...state,
      };
  }
};

export default movieReducer;
